import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { generateRecipe, generateMealPlan, analyzeFood, type RecipeGenerationParams, type MealPlanParams } from "./services/ai";
import { insertRecipeSchema, insertCalorieEntrySchema, insertMealPlanSchema, insertPantryItemSchema, insertShoppingListSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User profile routes
  app.patch('/api/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.updateUserProfile(userId, req.body);
      res.json(user);
    } catch (error) {
      console.error("Error updating profile:", error);
      res.status(500).json({ message: "Failed to update profile" });
    }
  });

  // Recipe routes
  app.post('/api/recipes/generate', isAuthenticated, async (req: any, res) => {
    try {
      const params: RecipeGenerationParams = req.body;
      const generatedRecipe = await generateRecipe(params);

      // Save recipe to database
      const userId = req.user.claims.sub;
      const recipe = await storage.createRecipe({
        userId,
        title: generatedRecipe.title,
        description: generatedRecipe.description,
        ingredients: generatedRecipe.ingredients,
        instructions: generatedRecipe.instructions,
        prepTime: generatedRecipe.prepTime,
        cookTime: generatedRecipe.cookTime,
        servings: generatedRecipe.servings,
        calories: generatedRecipe.calories,
        macros: generatedRecipe.macros,
        tags: generatedRecipe.tags,
        chefMode: params.chefMode,
        difficulty: generatedRecipe.difficulty,
        cuisine: generatedRecipe.cuisine,
        mealType: params.mealType || null,
      });

      res.json(recipe);
    } catch (error) {
      console.error("Error generating recipe:", error);
      res.status(500).json({ message: "Failed to generate recipe" });
    }
  });

  app.get('/api/recipes', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const recipes = await storage.getUserRecipes(userId);
      res.json(recipes);
    } catch (error) {
      console.error("Error fetching recipes:", error);
      res.status(500).json({ message: "Failed to fetch recipes" });
    }
  });

  app.get('/api/recipes/:id', isAuthenticated, async (req, res) => {
    try {
      const recipe = await storage.getRecipe(req.params.id);
      if (!recipe) {
        return res.status(404).json({ message: "Recipe not found" });
      }
      res.json(recipe);
    } catch (error) {
      console.error("Error fetching recipe:", error);
      res.status(500).json({ message: "Failed to fetch recipe" });
    }
  });

  app.delete('/api/recipes/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deleteRecipe(req.params.id);
      res.json({ message: "Recipe deleted successfully" });
    } catch (error) {
      console.error("Error deleting recipe:", error);
      res.status(500).json({ message: "Failed to delete recipe" });
    }
  });

  // Saved recipes routes
  app.post('/api/recipes/:id/save', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const recipeId = req.params.id;
      const { collectionName } = req.body;

      const savedRecipe = await storage.saveRecipe({
        userId,
        recipeId,
        collectionName: collectionName || "favorites",
      });

      res.json(savedRecipe);
    } catch (error) {
      console.error("Error saving recipe:", error);
      res.status(500).json({ message: "Failed to save recipe" });
    }
  });

  app.get('/api/saved-recipes', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const savedRecipes = await storage.getUserSavedRecipes(userId);
      res.json(savedRecipes);
    } catch (error) {
      console.error("Error fetching saved recipes:", error);
      res.status(500).json({ message: "Failed to fetch saved recipes" });
    }
  });

  app.delete('/api/recipes/:id/save', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const recipeId = req.params.id;
      await storage.unsaveRecipe(userId, recipeId);
      res.json({ message: "Recipe unsaved successfully" });
    } catch (error) {
      console.error("Error unsaving recipe:", error);
      res.status(500).json({ message: "Failed to unsave recipe" });
    }
  });

  // Meal plan routes
  app.post('/api/meal-plans/generate', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(400).json({ message: "User profile not found. Please complete your profile first." });
      }

      const params: MealPlanParams = {
        ...req.body,
        userStats: {
          age: user.age || 30,
          gender: user.gender || 'male',
          weight: parseFloat(user.weight?.toString() || '70'),
          height: user.height || 175,
        },
      };

      const generatedMealPlan = await generateMealPlan(params);

      // Save meal plan to database
      const mealPlan = await storage.createMealPlan({
        userId,
        name: `${params.days}-Day ${params.goal} Plan`,
        startDate: new Date(),
        endDate: new Date(Date.now() + params.days * 24 * 60 * 60 * 1000),
        goal: params.goal,
        dietaryRestrictions: params.dietaryRestrictions,
        meals: generatedMealPlan,
        shoppingList: generatedMealPlan.shoppingList,
      });

      res.json(mealPlan);
    } catch (error) {
      console.error("Error generating meal plan:", error);
      res.status(500).json({ message: "Failed to generate meal plan" });
    }
  });

  app.get('/api/meal-plans', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const mealPlans = await storage.getUserMealPlans(userId);
      res.json(mealPlans);
    } catch (error) {
      console.error("Error fetching meal plans:", error);
      res.status(500).json({ message: "Failed to fetch meal plans" });
    }
  });

  app.get('/api/meal-plans/:id', isAuthenticated, async (req, res) => {
    try {
      const mealPlan = await storage.getMealPlan(req.params.id);
      if (!mealPlan) {
        return res.status(404).json({ message: "Meal plan not found" });
      }
      res.json(mealPlan);
    } catch (error) {
      console.error("Error fetching meal plan:", error);
      res.status(500).json({ message: "Failed to fetch meal plan" });
    }
  });

  // Calorie tracking routes
  app.post('/api/calories/analyze-photo', isAuthenticated, async (req: any, res) => {
    try {
      const { image } = req.body;
      const analysis = await analyzeFood(image);
      res.json(analysis);
    } catch (error) {
      console.error("Error analyzing food photo:", error);
      res.status(500).json({ message: "Failed to analyze food photo" });
    }
  });

  app.post('/api/calories', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const entryData = insertCalorieEntrySchema.parse({
        ...req.body,
        userId,
      });

      const entry = await storage.createCalorieEntry(entryData);
      res.json(entry);
    } catch (error) {
      console.error("Error creating calorie entry:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create calorie entry" });
    }
  });

  app.get('/api/calories', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { startDate, endDate } = req.query;

      const entries = await storage.getUserCalorieEntries(
        userId,
        startDate ? new Date(startDate as string) : undefined,
        endDate ? new Date(endDate as string) : undefined
      );

      res.json(entries);
    } catch (error) {
      console.error("Error fetching calorie entries:", error);
      res.status(500).json({ message: "Failed to fetch calorie entries" });
    }
  });

  app.delete('/api/calories/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deleteCalorieEntry(req.params.id);
      res.json({ message: "Calorie entry deleted successfully" });
    } catch (error) {
      console.error("Error deleting calorie entry:", error);
      res.status(500).json({ message: "Failed to delete calorie entry" });
    }
  });

  // Pantry routes
  app.post('/api/pantry', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const itemData = insertPantryItemSchema.parse({
        ...req.body,
        userId,
      });

      const item = await storage.createPantryItem(itemData);
      res.json(item);
    } catch (error) {
      console.error("Error creating pantry item:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create pantry item" });
    }
  });

  app.get('/api/pantry', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const items = await storage.getUserPantryItems(userId);
      res.json(items);
    } catch (error) {
      console.error("Error fetching pantry items:", error);
      res.status(500).json({ message: "Failed to fetch pantry items" });
    }
  });

  app.patch('/api/pantry/:id', isAuthenticated, async (req, res) => {
    try {
      const item = await storage.updatePantryItem(req.params.id, req.body);
      res.json(item);
    } catch (error) {
      console.error("Error updating pantry item:", error);
      res.status(500).json({ message: "Failed to update pantry item" });
    }
  });

  app.delete('/api/pantry/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deletePantryItem(req.params.id);
      res.json({ message: "Pantry item deleted successfully" });
    } catch (error) {
      console.error("Error deleting pantry item:", error);
      res.status(500).json({ message: "Failed to delete pantry item" });
    }
  });

  // Shopping list routes
  app.post('/api/shopping-lists', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const listData = insertShoppingListSchema.parse({
        ...req.body,
        userId,
      });

      const list = await storage.createShoppingList(listData);
      res.json(list);
    } catch (error) {
      console.error("Error creating shopping list:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create shopping list" });
    }
  });

  app.get('/api/shopping-lists', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const lists = await storage.getUserShoppingLists(userId);
      res.json(lists);
    } catch (error) {
      console.error("Error fetching shopping lists:", error);
      res.status(500).json({ message: "Failed to fetch shopping lists" });
    }
  });

  app.patch('/api/shopping-lists/:id', isAuthenticated, async (req, res) => {
    try {
      const list = await storage.updateShoppingList(req.params.id, req.body);
      res.json(list);
    } catch (error) {
      console.error("Error updating shopping list:", error);
      res.status(500).json({ message: "Failed to update shopping list" });
    }
  });

  app.delete('/api/shopping-lists/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deleteShoppingList(req.params.id);
      res.json({ message: "Shopping list deleted successfully" });
    } catch (error) {
      console.error("Error deleting shopping list:", error);
      res.status(500).json({ message: "Failed to delete shopping list" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  recipes,
  savedRecipes,
  calorieEntries,
  mealPlans,
  pantryItems,
  shoppingLists,
  type User,
  type UpsertUser,
  type Recipe,
  type InsertRecipe,
  type SavedRecipe,
  type InsertSavedRecipe,
  type CalorieEntry,
  type InsertCalorieEntry,
  type MealPlan,
  type InsertMealPlan,
  type PantryItem,
  type InsertPantryItem,
  type ShoppingList,
  type InsertShoppingList,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, gte, lte } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserProfile(id: string, data: Partial<UpsertUser>): Promise<User>;

  // Recipe operations
  createRecipe(recipe: InsertRecipe): Promise<Recipe>;
  getRecipe(id: string): Promise<Recipe | undefined>;
  getUserRecipes(userId: string): Promise<Recipe[]>;
  updateRecipe(id: string, data: Partial<InsertRecipe>): Promise<Recipe>;
  deleteRecipe(id: string): Promise<void>;
  searchRecipes(query: string, userId?: string): Promise<Recipe[]>;

  // Saved recipes operations
  saveRecipe(data: InsertSavedRecipe): Promise<SavedRecipe>;
  getUserSavedRecipes(userId: string): Promise<(SavedRecipe & { recipe: Recipe })[]>;
  unsaveRecipe(userId: string, recipeId: string): Promise<void>;

  // Calorie tracking operations
  createCalorieEntry(entry: InsertCalorieEntry): Promise<CalorieEntry>;
  getUserCalorieEntries(userId: string, startDate?: Date, endDate?: Date): Promise<CalorieEntry[]>;
  updateCalorieEntry(id: string, data: Partial<InsertCalorieEntry>): Promise<CalorieEntry>;
  deleteCalorieEntry(id: string): Promise<void>;

  // Meal plan operations
  createMealPlan(mealPlan: InsertMealPlan): Promise<MealPlan>;
  getUserMealPlans(userId: string): Promise<MealPlan[]>;
  getMealPlan(id: string): Promise<MealPlan | undefined>;
  updateMealPlan(id: string, data: Partial<InsertMealPlan>): Promise<MealPlan>;
  deleteMealPlan(id: string): Promise<void>;

  // Pantry operations
  createPantryItem(item: InsertPantryItem): Promise<PantryItem>;
  getUserPantryItems(userId: string): Promise<PantryItem[]>;
  updatePantryItem(id: string, data: Partial<InsertPantryItem>): Promise<PantryItem>;
  deletePantryItem(id: string): Promise<void>;

  // Shopping list operations
  createShoppingList(list: InsertShoppingList): Promise<ShoppingList>;
  getUserShoppingLists(userId: string): Promise<ShoppingList[]>;
  updateShoppingList(id: string, data: Partial<InsertShoppingList>): Promise<ShoppingList>;
  deleteShoppingList(id: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserProfile(id: string, data: Partial<UpsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Recipe operations
  async createRecipe(recipe: InsertRecipe): Promise<Recipe> {
    const [newRecipe] = await db.insert(recipes).values(recipe).returning();
    return newRecipe;
  }

  async getRecipe(id: string): Promise<Recipe | undefined> {
    const [recipe] = await db.select().from(recipes).where(eq(recipes.id, id));
    return recipe;
  }

  async getUserRecipes(userId: string): Promise<Recipe[]> {
    return await db
      .select()
      .from(recipes)
      .where(eq(recipes.userId, userId))
      .orderBy(desc(recipes.createdAt));
  }

  async updateRecipe(id: string, data: Partial<InsertRecipe>): Promise<Recipe> {
    const [recipe] = await db
      .update(recipes)
      .set(data)
      .where(eq(recipes.id, id))
      .returning();
    return recipe;
  }

  async deleteRecipe(id: string): Promise<void> {
    await db.delete(recipes).where(eq(recipes.id, id));
  }

  async searchRecipes(query: string, userId?: string): Promise<Recipe[]> {
    const conditions = [];
    if (userId) {
      conditions.push(eq(recipes.userId, userId));
    }

    return await db
      .select()
      .from(recipes)
      .where(and(...conditions))
      .orderBy(desc(recipes.createdAt));
  }

  // Saved recipes operations
  async saveRecipe(data: InsertSavedRecipe): Promise<SavedRecipe> {
    const [savedRecipe] = await db.insert(savedRecipes).values(data).returning();
    return savedRecipe;
  }

  async getUserSavedRecipes(userId: string): Promise<(SavedRecipe & { recipe: Recipe })[]> {
    return await db
      .select({
        id: savedRecipes.id,
        userId: savedRecipes.userId,
        recipeId: savedRecipes.recipeId,
        collectionName: savedRecipes.collectionName,
        savedAt: savedRecipes.savedAt,
        recipe: recipes,
      })
      .from(savedRecipes)
      .innerJoin(recipes, eq(savedRecipes.recipeId, recipes.id))
      .where(eq(savedRecipes.userId, userId))
      .orderBy(desc(savedRecipes.savedAt));
  }

  async unsaveRecipe(userId: string, recipeId: string): Promise<void> {
    await db
      .delete(savedRecipes)
      .where(and(eq(savedRecipes.userId, userId), eq(savedRecipes.recipeId, recipeId)));
  }

  // Calorie tracking operations
  async createCalorieEntry(entry: InsertCalorieEntry): Promise<CalorieEntry> {
    const [calorieEntry] = await db.insert(calorieEntries).values(entry).returning();
    return calorieEntry;
  }

  async getUserCalorieEntries(
    userId: string,
    startDate?: Date,
    endDate?: Date
  ): Promise<CalorieEntry[]> {
    const conditions = [eq(calorieEntries.userId, userId)];

    if (startDate) {
      conditions.push(gte(calorieEntries.date, startDate));
    }
    if (endDate) {
      conditions.push(lte(calorieEntries.date, endDate));
    }

    return await db
      .select()
      .from(calorieEntries)
      .where(and(...conditions))
      .orderBy(desc(calorieEntries.date));
  }

  async updateCalorieEntry(id: string, data: Partial<InsertCalorieEntry>): Promise<CalorieEntry> {
    const [entry] = await db
      .update(calorieEntries)
      .set(data)
      .where(eq(calorieEntries.id, id))
      .returning();
    return entry;
  }

  async deleteCalorieEntry(id: string): Promise<void> {
    await db.delete(calorieEntries).where(eq(calorieEntries.id, id));
  }

  // Meal plan operations
  async createMealPlan(mealPlan: InsertMealPlan): Promise<MealPlan> {
    const [newMealPlan] = await db.insert(mealPlans).values(mealPlan).returning();
    return newMealPlan;
  }

  async getUserMealPlans(userId: string): Promise<MealPlan[]> {
    return await db
      .select()
      .from(mealPlans)
      .where(eq(mealPlans.userId, userId))
      .orderBy(desc(mealPlans.createdAt));
  }

  async getMealPlan(id: string): Promise<MealPlan | undefined> {
    const [mealPlan] = await db.select().from(mealPlans).where(eq(mealPlans.id, id));
    return mealPlan;
  }

  async updateMealPlan(id: string, data: Partial<InsertMealPlan>): Promise<MealPlan> {
    const [mealPlan] = await db
      .update(mealPlans)
      .set(data)
      .where(eq(mealPlans.id, id))
      .returning();
    return mealPlan;
  }

  async deleteMealPlan(id: string): Promise<void> {
    await db.delete(mealPlans).where(eq(mealPlans.id, id));
  }

  // Pantry operations
  async createPantryItem(item: InsertPantryItem): Promise<PantryItem> {
    const [pantryItem] = await db.insert(pantryItems).values(item).returning();
    return pantryItem;
  }

  async getUserPantryItems(userId: string): Promise<PantryItem[]> {
    return await db
      .select()
      .from(pantryItems)
      .where(eq(pantryItems.userId, userId))
      .orderBy(asc(pantryItems.name));
  }

  async updatePantryItem(id: string, data: Partial<InsertPantryItem>): Promise<PantryItem> {
    const [item] = await db
      .update(pantryItems)
      .set(data)
      .where(eq(pantryItems.id, id))
      .returning();
    return item;
  }

  async deletePantryItem(id: string): Promise<void> {
    await db.delete(pantryItems).where(eq(pantryItems.id, id));
  }

  // Shopping list operations
  async createShoppingList(list: InsertShoppingList): Promise<ShoppingList> {
    const [shoppingList] = await db.insert(shoppingLists).values(list).returning();
    return shoppingList;
  }

  async getUserShoppingLists(userId: string): Promise<ShoppingList[]> {
    return await db
      .select()
      .from(shoppingLists)
      .where(eq(shoppingLists.userId, userId))
      .orderBy(desc(shoppingLists.createdAt));
  }

  async updateShoppingList(id: string, data: Partial<InsertShoppingList>): Promise<ShoppingList> {
    const [list] = await db
      .update(shoppingLists)
      .set(data)
      .where(eq(shoppingLists.id, id))
      .returning();
    return list;
  }

  async deleteShoppingList(id: string): Promise<void> {
    await db.delete(shoppingLists).where(eq(shoppingLists.id, id));
  }
}

export const storage = new DatabaseStorage();

