@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Chef color scheme */
  --chef-orange: hsl(22, 93%, 53%);
  --chef-red: hsl(0, 85%, 60%);
  --chef-gray: hsl(210, 40%, 98%);

  /* Updated color system */
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(222, 84%, 5%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 84%, 5%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 84%, 5%);
  --primary: hsl(22, 93%, 53%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(210, 40%, 98%);
  --secondary-foreground: hsl(222, 84%, 5%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215, 16%, 47%);
  --accent: hsl(210, 40%, 96%);
  --accent-foreground: hsl(222, 84%, 5%);
  --destructive: hsl(0, 85%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --ring: hsl(22, 93%, 53%);
  --radius: 0.75rem;
  --chart-1: hsl(22, 93%, 53%);
  --chart-2: hsl(0, 85%, 60%);
  --chart-3: hsl(45, 93%, 47%);
  --chart-4: hsl(120, 50%, 45%);
  --chart-5: hsl(240, 50%, 60%);

  /* Sidebar colors */
  --sidebar-background: hsl(0, 0%, 98%);
  --sidebar-foreground: hsl(222, 84%, 5%);
  --sidebar-primary: hsl(22, 93%, 53%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(210, 40%, 96%);
  --sidebar-accent-foreground: hsl(222, 84%, 5%);
  --sidebar-border: hsl(214, 32%, 91%);
  --sidebar-ring: hsl(22, 93%, 53%);
}

.dark {
  --background: hsl(222, 84%, 5%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 5%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222, 84%, 5%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(22, 93%, 53%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(217, 32%, 17%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62%, 50%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --ring: hsl(22, 93%, 53%);

  --sidebar-background: hsl(222, 84%, 5%);
  --sidebar-foreground: hsl(210, 40%, 98%);
  --sidebar-primary: hsl(22, 93%, 53%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(217, 32%, 17%);
  --sidebar-accent-foreground: hsl(210, 40%, 98%);
  --sidebar-border: hsl(217, 32%, 17%);
  --sidebar-ring: hsl(22, 93%, 53%);
}

* {
  border-color: var(--border);
}

body {
  background-color: var(--background);
  color: var(--foreground);
  font-family: 'Inter', system-ui, sans-serif;
}

/* Custom scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(210, 40%, 98%);
}

::-webkit-scrollbar-thumb {
  background: hsl(22, 93%, 53%);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(22, 93%, 45%);
}

/* Smooth transitions */
* {
  transition-property: color, background-color, border-color, text-decoration-color, fill, stroke;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  transition-duration: 150ms;
}

/* Food image aspect ratio */
.food-image {
  aspect-ratio: 16 / 10;
  object-fit: cover;
}

/* Recipe card hover effects */
.recipe-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.recipe-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 10px 10px -5px rgb(0 0 0 / 0.04);
}

/* Button ripple effect */
.ripple {
  position: absolute;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  transform: scale(0);
  animation: ripple-animation 0.6s linear;
  pointer-events: none;
}

@keyframes ripple-animation {
  to {
    transform: scale(4);
    opacity: 0;
  }
}

/* Navigation scroll effect */
.nav-scrolled {
  backdrop-filter: blur(10px);
  background-color: rgba(255, 255, 255, 0.95);
  border-bottom: 1px solid hsl(214, 32%, 91%);
}

/* Loading spinner */
.loading-spinner {
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Ingredient tags */
.ingredient-tag {
  background: hsl(22, 93%, 53%, 0.1);
  color: hsl(22, 93%, 53%);
  border: 1px solid hsl(22, 93%, 53%, 0.2);
}

.ingredient-tag:hover {
  background: hsl(22, 93%, 53%, 0.15);
}

/* Chef mode gradient backgrounds */
.pantry-gradient {
  background: linear-gradient(135deg, hsl(45, 93%, 47%) 0%, hsl(22, 93%, 53%) 100%);
}

.master-gradient {
  background: linear-gradient(135deg, hsl(0, 85%, 60%) 0%, hsl(340, 75%, 55%) 100%);
}

.macros-gradient {
  background: linear-gradient(135deg, hsl(120, 50%, 45%) 0%, hsl(160, 50%, 50%) 100%);
}

.mixology-gradient {
  background: linear-gradient(135deg, hsl(240, 50%, 60%) 0%, hsl(270, 50%, 55%) 100%);
}

.meal-plan-gradient {
  background: linear-gradient(135deg, hsl(22, 93%, 53%) 0%, hsl(45, 93%, 47%) 100%);
}

/* Testimonial cards */
.testimonial-card {
  backdrop-filter: blur(10px);
  background-color: rgba(255, 255, 255, 0.9);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* Pricing card effects */
.pricing-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

.pricing-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
}

.pricing-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 2px;
  background: linear-gradient(90deg, transparent, hsl(22, 93%, 53%), transparent);
  animation: shimmer 2s infinite;
}

@keyframes shimmer {
  0% {
    left: -100%;
  }
  100% {
    left: 100%;
  }
}

/* Form input focus styles */
.form-input:focus {
  ring-color: hsl(22, 93%, 53%);
  border-color: hsl(22, 93%, 53%);
}

/* Mobile responsive adjustments */
@media (max-width: 768px) {
  .recipe-card:hover {
    transform: none;
  }

  .pricing-card:hover {
    transform: none;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }
}

/* Accessibility improvements */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  .recipe-card:hover,
  .pricing-card:hover {
    transform: none;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  :root {
    --border: hsl(0, 0%, 20%);
    --muted: hsl(0, 0%, 90%);
  }

  .dark {
    --border: hsl(0, 0%, 80%);
    --muted: hsl(0, 0%, 10%);
  }
}

