interface MealPlanCalendarProps {
  mealPlan?: {
    id: string;
    name: string;
    startDate: string;
    endDate: string;
    meals: Array<{
      date: string;
      breakfast?: string;
      lunch?: string;
      dinner?: string;
      snacks?: string[];
    }>;
    targetCalories?: number;
  };
  onAddMeal?: (date: string, mealType: string) => void;
  onEditMeal?: (date: string, mealType: string, currentMeal?: string) => void;
}

const DAYS_OF_WEEK = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

const MEAL_TYPES = [
  { key: 'breakfast', label: 'Breakfast', icon: Coffee, color: 'bg-yellow-100 text-yellow-800' },
  { key: 'lunch', label: 'Lunch', icon: Sun, color: 'bg-orange-100 text-orange-800' },
  { key: 'dinner', label: 'Dinner', icon: Moon, color: 'bg-purple-100 text-purple-800' },
  { key: 'snacks', label: 'Snacks', icon: Cookie, color: 'bg-green-100 text-green-800' },
];

export default function MealPlanCalendar({ 
  mealPlan, 
  onAddMeal, 
  onEditMeal 
}: MealPlanCalendarProps) {
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const today = new Date();
    const monday = new Date(today);
    monday.setDate(today.getDate() - today.getDay() + 1);
    return monday;
  });

  // Generate week dates
  const weekDates = Array.from({ length: 7 }, (_, i) => {
    const date = new Date(currentWeekStart);
    date.setDate(currentWeekStart.getDate() + i);
    return date;
  });

  const navigateWeek = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentWeekStart);
    newDate.setDate(currentWeekStart.getDate() + (direction === 'next' ? 7 : -7));
    setCurrentWeekStart(newDate);
  };

  const formatDate = (date: Date) => {
    return date.toISOString().split('T')[0];
  };

  const getMealForDate = (date: Date, mealType: string) => {
    if (!mealPlan) return null;

    const dateStr = formatDate(date);
    const dayMeals = mealPlan.meals.find(m => m.date === dateStr);

    if (!dayMeals) return null;

    if (mealType === 'snacks') {
      return dayMeals.snacks?.join(', ') || null;
    }

    return dayMeals[mealType as keyof typeof dayMeals] || null;
  };

  const isToday = (date: Date) => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  const isPastDate = (date: Date) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return date < today;
  };

  if (!mealPlan) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <Calendar className="h-12 w-12 mx-auto mb-4 text-gray-400" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Meal Plan</h3>
          <p className="text-gray-600 mb-4">Create a meal plan to see your weekly schedule</p>
          <Button className="bg-chef-orange hover:bg-orange-600">
            Create Meal Plan
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Calendar Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center">
                <Calendar className="h-5 w-5 mr-2" />
                {mealPlan.name}
              </CardTitle>
              <p className="text-sm text-gray-600 mt-1">
                {new Date(mealPlan.startDate).toLocaleDateString()} - {new Date(mealPlan.endDate).toLocaleDateString()}
              </p>
            </div>
            <div className="flex items-center space-x-2">
              <Button variant="outline" size="icon" onClick={() => navigateWeek('prev')}>
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <span className="text-sm font-medium min-w-[120px] text-center">
                {currentWeekStart.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} - {
                  (() => {
                    const weekEnd = new Date(currentWeekStart);
                    weekEnd.setDate(currentWeekStart.getDate() + 6);
                    return weekEnd.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
                  })()
                }
              </span>
              <Button variant="outline" size="icon" onClick={() => navigateWeek('next')}>
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Weekly Calendar Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-7 gap-4">
        {weekDates.map((date, dayIndex) => (
          <Card 
            key={dayIndex}
            className={`${isToday(date) ? 'ring-2 ring-chef-orange' : ''} ${
              isPastDate(date) ? 'opacity-60' : ''
            }`}
          >
            <CardHeader className="pb-3">
              <div className="text-center">
                <p className="text-sm font-medium text-gray-600">
                  {DAYS_OF_WEEK[dayIndex]}
                </p>
                <p className={`text-lg font-bold ${
                  isToday(date) ? 'text-chef-orange' : 'text-gray-900'
                }`}>
                  {date.getDate()}
                </p>
                {isToday(date) && (
                  <Badge className="bg-chef-orange text-white text-xs mt-1">
                    Today
                  </Badge>
                )}
              </div>
            </CardHeader>

            <CardContent className="pt-0 space-y-3">
              {MEAL_TYPES.map((mealType) => {
                const meal = getMealForDate(date, mealType.key);
                const IconComponent = mealType.icon;

                return (
                  <div key={mealType.key} className="space-y-1">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <IconComponent className="h-3 w-3 mr-1" />
                        <span className="text-xs font-medium text-gray-700">
                          {mealType.label}
                        </span>
                      </div>
                      {onAddMeal && (
                        <Button
                          size="icon"
                          variant="ghost"
                          className="h-5 w-5"
                          onClick={() => onAddMeal(formatDate(date), mealType.key)}
                        >
                          <Plus className="h-3 w-3" />
                        </Button>
                      )}
                    </div>

                    {meal ? (
                      <div
                        className={`text-xs p-2 rounded cursor-pointer hover:opacity-80 transition-opacity ${mealType.color}`}
                        onClick={() => onEditMeal?.(formatDate(date), mealType.key, meal)}
                      >
                        {meal.length > 30 ? `${meal.substring(0, 30)}...` : meal}
                      </div>
                    ) : (
                      <div 
                        className="text-xs p-2 rounded border-2 border-dashed border-gray-200 text-gray-400 cursor-pointer hover:border-chef-orange hover:text-chef-orange transition-colors"
                        onClick={() => onAddMeal?.(formatDate(date), mealType.key)}
                      >
                        + Add {mealType.label.toLowerCase()}
                      </div>
                    )}
                  </div>
                );
              })}

              {/* Daily Calories */}
              {mealPlan.targetCalories && (
                <div className="pt-2 border-t">
                  <div className="flex items-center justify-between text-xs">
                    <span className="text-gray-600">Target:</span>
                    <span className="font-medium">
                      {Math.round(mealPlan.targetCalories)} cal
                    </span>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Legend */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center justify-center space-x-6 text-xs">
            {MEAL_TYPES.map((mealType) => {
              const IconComponent = mealType.icon;
              return (
                <div key={mealType.key} className="flex items-center">
                  <IconComponent className="h-3 w-3 mr-1" />
                  <span className={`px-2 py-1 rounded ${mealType.color}`}>
                    {mealType.label}
                  </span>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/meal-plan-card.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Calendar, Users, Target, ShoppingCart, Eye } from "lucide-react";
import type { MealPlan } from "@shared/schema";

interface MealPlanCardProps {
  mealPlan: MealPlan;
  onView?: (mealPlanId: string) => void;
  onAddToShopping?: (mealPlanId: string) => void;
  showActions?: boolean;
}

export default function MealPlanCard({ 
  mealPlan, 
  onView, 
  onAddToShopping,
  showActions = true 
}: MealPlanCardProps) {
  const startDate = new Date(mealPlan.startDate);
  const endDate = new Date(mealPlan.endDate);
  const duration = Math.ceil((endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));

  const meals = mealPlan.meals as any[];
  const totalMeals = meals?.length || 0;

  // Calculate total nutrition if meals data is available
  const totalCalories = meals?.reduce((acc, meal) => acc + (meal.recipe?.calories || 0), 0) || 0;
  const avgCaloriesPerDay = totalCalories / (duration || 1);

  return (
    <Card className="hover:shadow-lg transition-shadow duration-200 group">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-lg mb-2 group-hover:text-chef-orange transition-colors">
              {mealPlan.name}
            </CardTitle>
            <div className="flex items-center gap-4 text-sm text-gray-600">
              <div className="flex items-center gap-1">
                <Calendar className="w-4 h-4" />
                <span>{startDate.toLocaleDateString()}</span>
              </div>
              <div className="flex items-center gap-1">
                <span>{duration} days</span>
              </div>
            </div>
          </div>
          {mealPlan.goal && (
            <Badge className="bg-chef-orange/10 text-chef-orange">
              {mealPlan.goal}
            </Badge>
          )}
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Stats */}
        <div className="grid grid-cols-3 gap-4 text-sm">
          <div className="text-center p-2 bg-gray-50 rounded">
            <div className="font-medium text-chef-orange">{totalMeals}</div>
            <div className="text-gray-600">Total Meals</div>
          </div>
          <div className="text-center p-2 bg-gray-50 rounded">
            <div className="font-medium text-chef-orange">{Math.round(avgCaloriesPerDay)}</div>
            <div className="text-gray-600">Cal/Day</div>
          </div>
          <div className="text-center p-2 bg-gray-50 rounded">
            <div className="font-medium text-chef-orange">{mealPlan.shoppingList?.length || 0}</div>
            <div className="text-gray-600">Ingredients</div>
          </div>
        </div>

        {/* Dietary Restrictions */}
        {mealPlan.dietaryRestrictions && mealPlan.dietaryRestrictions.length > 0 && (
          <div>
            <h4 className="text-sm font-medium mb-2">Dietary Preferences</h4>
            <div className="flex flex-wrap gap-1">
              {mealPlan.dietaryRestrictions.map((restriction, index) => (
                <Badge key={index} variant="outline" className="text-xs">
                  {restriction}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Sample Meals Preview */}
        {meals && meals.length > 0 && (
          <div>
            <h4 className="text-sm font-medium mb-2">Sample Meals</h4>
            <div className="space-y-1">
              {meals.slice(0, 3).map((meal, index) => (
                <div key={index} className="flex justify-between text-xs">
                  <span className="text-gray-600">
                    Day {meal.day} â€¢ {meal.mealType}
                  </span>
                  <span className="font-medium">{meal.recipe?.title}</span>
                </div>
              ))}
              {meals.length > 3 && (
                <div className="text-xs text-gray-500">
                  +{meals.length - 3} more meals...
                </div>
              )}
            </div>
          </div>
        )}

        {/* Actions */}
        {showActions && (
          <div className="flex gap-2 pt-2">
            <Button 
              variant="outline" 
              size="sm" 
              className="flex-1"
              onClick={() => onView?.(mealPlan.id)}
            >
              <Eye className="w-4 h-4 mr-1" />
              View Plan
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => onAddToShopping?.(mealPlan.id)}
            >
              <ShoppingCart className="w-4 h-4" />
            </Button>
          </div>
        )}

        {/* Created Date */}
        <div className="text-xs text-gray-500 pt-2 border-t">
          Created {new Date(mealPlan.createdAt!).toLocaleDateString()}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/multi-step-form.ts.content:
import { ReactNode, useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { ChevronLeft, ChevronRight } from "lucide-react";

interface Step {
  id: string;
  title: string;
  description?: string;
  content: ReactNode;
  isValid?: boolean;
}

interface MultiStepFormProps {
  steps: Step[];
  onComplete: () => void;
  onStepChange?: (stepIndex: number) => void;
  isSubmitting?: boolean;
  submitButtonText?: string;
  className?: string;
}

export default function MultiStepForm({
  steps,
  onComplete,
  onStepChange,
  isSubmitting = false,
  submitButtonText = "Complete",
  className = ""
}: MultiStepFormProps) {
  const [currentStep, setCurrentStep] = useState(0);

  const progress = ((currentStep + 1) / steps.length) * 100;
  const isLastStep = currentStep === steps.length - 1;
  const isFirstStep = currentStep === 0;
  const currentStepData = steps[currentStep];

  const handleNext = () => {
    if (isLastStep) {
      onComplete();
    } else {
      const nextStep = currentStep + 1;
      setCurrentStep(nextStep);
      onStepChange?.(nextStep);
    }
  };

  const handlePrevious = () => {
    if (!isFirstStep) {
      const prevStep = currentStep - 1;
      setCurrentStep(prevStep);
      onStepChange?.(prevStep);
    }
  };

  const handleStepClick = (stepIndex: number) => {
    setCurrentStep(stepIndex);
    onStepChange?.(stepIndex);
  };

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Progress Bar */}
      <div className="space-y-2">
        <div className="flex justify-between text-sm text-gray-600">
          <span>Step {currentStep + 1} of {steps.length}</span>
          <span>{Math.round(progress)}% Complete</span>
        </div>
        <Progress value={progress} className="h-2" />
      </div>

      {/* Step Navigation */}
      <div className="flex space-x-2 overflow-x-auto pb-2">
        {steps.map((step, index) => (
          <button
            key={step.id}
            onClick={() => handleStepClick(index)}
            className={`flex items-center space-x-2 px-3 py-2 rounded-lg text-sm font-medium whitespace-nowrap transition-colors ${
              index === currentStep
                ? "bg-chef-orange text-white"
                : index < currentStep
                ? "bg-green-100 text-green-700 hover:bg-green-200"
                : "bg-gray-100 text-gray-500 hover:bg-gray-200"
            }`}
          >
            <div className={`flex items-center justify-center w-6 h-6 rounded-full text-xs font-bold ${
              index === currentStep
                ? "bg-white text-chef-orange"
                : index < currentStep
                ? "bg-green-500 text-white"
                : "bg-gray-300 text-gray-600"
            }`}>
              {index + 1}
            </div>
            <span>{step.title}</span>
          </button>
        ))}
      </div>

      {/* Current Step Content */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-3">
            <div className="flex items-center justify-center w-10 h-10 bg-chef-orange text-white rounded-xl font-bold text-lg">
              {currentStep + 1}
            </div>
            <span>{currentStepData.title}</span>
          </CardTitle>
          {currentStepData.description && (
            <CardDescription>{currentStepData.description}</CardDescription>
          )}
        </CardHeader>
        <CardContent>
          {currentStepData.content}
        </CardContent>
      </Card>

      {/* Navigation Buttons */}
      <div className="flex justify-between">
        <Button
          variant="outline"
          onClick={handlePrevious}
          disabled={isFirstStep || isSubmitting}
        >
          <ChevronLeft className="h-4 w-4 mr-2" />
          Previous
        </Button>

        <Button
          onClick={handleNext}
          disabled={currentStepData.isValid === false || isSubmitting}
          className="bg-chef-orange hover:bg-chef-orange/90"
        >
          {isLastStep ? submitButtonText : "Next"}
          {!isLastStep && <ChevronRight className="h-4 w-4 ml-2" />}
        </Button>
      </div>
    </div>
  );
}

client/src/components/photo-upload.tsx.content:
import { useState, useRef, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Camera, Upload, X, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface PhotoUploadProps {
  onPhotoCapture: (file: File) => void;
  onPhotoAnalyze?: (file: File) => Promise<any>;
  isAnalyzing?: boolean;
  acceptedTypes?: string;
  maxSizeMB?: number;
  className?: string;
}

export default function PhotoUpload({
  onPhotoCapture,
  onPhotoAnalyze,
  isAnalyzing = false,
  acceptedTypes = "image/*",
  maxSizeMB = 10,
  className = ""
}: PhotoUploadProps) {
  const { toast } = useToast();
  const [preview, setPreview] = useState<string | null>(null);
  const [dragOver, setDragOver] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const cameraInputRef = useRef<HTMLInputElement>(null);

  const validateFile = (file: File): boolean => {
    // Check file type
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Invalid File Type",
        description: "Please select an image file.",
        variant: "destructive",
      });
      return false;
    }

    // Check file size
    const fileSizeMB = file.size / (1024 * 1024);
    if (fileSizeMB > maxSizeMB) {
      toast({
        title: "File Too Large",
        description: `File size must be less than ${maxSizeMB}MB.`,
        variant: "destructive",
      });
      return false;
    }

    return true;
  };

  const handleFile = useCallback((file: File) => {
    if (!validateFile(file)) return;

    // Create preview
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreview(e.target?.result as string);
    };
    reader.readAsDataURL(file);

    // Call the callback
    onPhotoCapture(file);

    // Auto-analyze if function provided
    if (onPhotoAnalyze) {
      onPhotoAnalyze(file);
    }
  }, [onPhotoCapture, onPhotoAnalyze]);

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      handleFile(file);
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);

    const file = e.dataTransfer.files[0];
    if (file) {
      handleFile(file);
    }
  };

  const clearPhoto = () => {
    setPreview(null);
    if (fileInputRef.current) fileInputRef.current.value = '';
    if (cameraInputRef.current) cameraInputRef.current.value = '';
  };

  const openFileDialog = () => {
    fileInputRef.current?.click();
  };

  const openCamera = () => {
    cameraInputRef.current?.click();
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Hidden file inputs */}
      <input
        ref={fileInputRef}
        type="file"
        accept={acceptedTypes}
        onChange={handleFileSelect}
        className="hidden"
      />
      <input
        ref={cameraInputRef}
        type="file"
        accept={acceptedTypes}
        capture="environment"
        onChange={handleFileSelect}
        className="hidden"
      />

      {/* Upload Area */}
      {!preview ? (
        <Card 
          className={`photo-upload-area cursor-pointer ${dragOver ? 'dragover' : ''}`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          onClick={openFileDialog}
        >
          <CardContent className="p-8 text-center">
            <div className="space-y-4">
              <div className="mx-auto w-16 h-16 bg-chef-orange/10 rounded-full flex items-center justify-center">
                <Camera className="h-8 w-8 text-chef-orange" />
              </div>

              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                  Take a Photo or Upload Image
                </h3>
                <p className="text-gray-600 mb-4">
                  Drag and drop an image here, or click to select
                </p>
                <div className="flex justify-center space-x-3">
                  <Button
                    type="button"
                    onClick={(e) => {
                      e.stopPropagation();
                      openCamera();
                    }}
                    className="bg-chef-orange hover:bg-chef-orange/90"
                  >
                    <Camera className="h-4 w-4 mr-2" />
                    Take Photo
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={(e) => {
                      e.stopPropagation();
                      openFileDialog();
                    }}
                  >
                    <Upload className="h-4 w-4 mr-2" />
                    Upload File
                  </Button>
                </div>
              </div>

              <p className="text-xs text-gray-500">
                Max file size: {maxSizeMB}MB. Supported formats: JPG, PNG, WebP
              </p>
            </div>
          </CardContent>
        </Card>
      ) : (
        /* Preview Area */
        <Card>
          <CardContent className="p-4">
            <div className="relative">
              <img
                src={preview}
                alt="Uploaded food"
                className="w-full h-64 object-cover rounded-lg"
              />

              {/* Loading overlay */}
              {isAnalyzing && (
                <div className="absolute inset-0 bg-black/50 rounded-lg flex items-center justify-center">
                  <div className="text-center text-white">
                    <Loader2 className="h-8 w-8 animate-spin mx-auto mb-2" />
                    <p className="text-sm">Analyzing food...</p>
                  </div>
                </div>
              )}

              {/* Remove button */}
              <Button
                size="sm"
                variant="destructive"
                className="absolute top-2 right-2"
                onClick={clearPhoto}
                disabled={isAnalyzing}
              >
                <X className="h-4 w-4" />
              </Button>
            </div>

            <div className="mt-4 flex space-x-2">
              <Button
                variant="outline"
                onClick={openCamera}
                disabled={isAnalyzing}
                className="flex-1"
              >
                <Camera className="h-4 w-4 mr-2" />
                Take Another
              </Button>
              <Button
                variant="outline"
                onClick={openFileDialog}
                disabled={isAnalyzing}
                className="flex-1"
              >
                <Upload className="h-4 w-4 mr-2" />
                Upload Different
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/recipe-card.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Clock, Users, Heart, BookOpen, ShoppingCart } from "lucide-react";
import type { Recipe } from "@shared/schema";

interface RecipeCardProps {
  recipe: Recipe;
  onSave?: (recipeId: string) => void;
  onAddToShopping?: (recipeId: string) => void;
  onView?: (recipeId: string) => void;
  showActions?: boolean;
}

export default function RecipeCard({ 
  recipe, 
  onSave, 
  onAddToShopping, 
  onView,
  showActions = true 
}: RecipeCardProps) {
  const totalTime = (recipe.prepTime || 0) + (recipe.cookTime || 0);
  const macros = recipe.macros as any;

  return (
    <Card className="hover:shadow-lg transition-shadow duration-200 group">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-lg mb-2 group-hover:text-chef-orange transition-colors">
              {recipe.title}
            </CardTitle>
            {recipe.description && (
              <p className="text-sm text-gray-600 line-clamp-2">{recipe.description}</p>
            )}
          </div>
          {recipe.imageUrl && (
            <img 
              src={recipe.imageUrl} 
              alt={recipe.title}
              className="w-16 h-16 rounded-lg object-cover ml-3"
            />
          )}
        </div>

        <div className="flex flex-wrap gap-2 mt-2">
          {recipe.cuisine && (
            <Badge variant="secondary" className="text-xs">
              {recipe.cuisine}
            </Badge>
          )}
          {recipe.difficulty && (
            <Badge 
              variant="outline" 
              className={`text-xs ${
                recipe.difficulty === 'easy' ? 'border-green-300 text-green-700' :
                recipe.difficulty === 'medium' ? 'border-yellow-300 text-yellow-700' :
                'border-red-300 text-red-700'
              }`}
            >
              {recipe.difficulty}
            </Badge>
          )}
          {recipe.mealType && (
            <Badge variant="outline" className="text-xs">
              {recipe.mealType}
            </Badge>
          )}
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Recipe Stats */}
        <div className="grid grid-cols-3 gap-4 text-sm">
          <div className="flex items-center gap-1">
            <Clock className="w-4 h-4 text-gray-500" />
            <span>{totalTime || 'N/A'} min</span>
          </div>
          <div className="flex items-center gap-1">
            <Users className="w-4 h-4 text-gray-500" />
            <span>{recipe.servings || 1} servings</span>
          </div>
          <div className="flex items-center gap-1">
            <span className="text-chef-orange font-medium">
              {recipe.calories || 0} cal
            </span>
          </div>
        </div>

        {/* Macros */}
        {macros && (
          <div className="grid grid-cols-3 gap-2 text-xs">
            <div className="text-center p-2 bg-gray-50 rounded">
              <div className="font-medium text-chef-orange">{macros.protein || 0}g</div>
              <div className="text-gray-600">Protein</div>
            </div>
            <div className="text-center p-2 bg-gray-50 rounded">
              <div className="font-medium text-chef-orange">{macros.carbs || 0}g</div>
              <div className="text-gray-600">Carbs</div>
            </div>
            <div className="text-center p-2 bg-gray-50 rounded">
              <div className="font-medium text-chef-orange">{macros.fat || 0}g</div>
              <div className="text-gray-600">Fat</div>
            </div>
          </div>
        )}

        {/* Tags */}
        {recipe.tags && recipe.tags.length > 0 && (
          <div className="flex flex-wrap gap-1">
            {recipe.tags.slice(0, 3).map((tag, index) => (
              <Badge key={index} variant="outline" className="text-xs">
                {tag}
              </Badge>
            ))}
            {recipe.tags.length > 3 && (
              <Badge variant="outline" className="text-xs">
                +{recipe.tags.length - 3} more
              </Badge>
            )}
          </div>
        )}

        {/* Actions */}
        {showActions && (
          <div className="flex gap-2 pt-2">
            <Button 
              variant="outline" 
              size="sm" 
              className="flex-1"
              onClick={() => onView?.(recipe.id)}
            >
              <BookOpen className="w-4 h-4 mr-1" />
              View
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => onSave?.(recipe.id)}
            >
              <Heart className="w-4 h-4" />
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => onAddToShopping?.(recipe.id)}
            >
              <ShoppingCart className="w-4 h-4" />
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

